#
# apt install a bunch of random stuff ...
#

include_recipe 'g3_dev_apt'

user 'gen3lab' do
  comment 'lab user'
  shell '/bin/bash'
  manage_home true
end

group 'gen3lab-docker' do
  action :manage
  group_name 'docker'
  members ['gen3lab']
  append true
end


execute 'g3-lab-setup' do
  cwd '/home/gen3lab'
  command <<-EOF
    (
      su gen3lab
      # command for installing Python goes here
      if [ ! -d ./compose-services ]; then
        git clone https://github.com/uc-cdis/compose-services.git
        cd ./compose-services
        bash ./creds_setup.sh
      fi
    )
    EOF
end

execute 'g3-lab-keys' do
  cwd '/tmp'
  # TODO - move the list of keys out to an attribute or databag ...
  command <<-EOF
(
  for dir in /home/ubuntu /home/gen3lab; do
    if [ -d "$dir" ]; then
      cd "$dir"
      if [ ! -f .ssh/authorized_keys ]; then
        mkdir -m 0700 -p .ssh
        touch .ssh/authorized_keys
        chown -R $(basename $dir): .ssh
        chmod -R 0700 .ssh
      fi
      (cat - <<EOM
ssh-rsa  ribeyre@uchicago.edu
ssh-dss  renukarya@Renukas-MacBook-Pro.local
ssh-rsa  rpowell@rpowell.local
ssh-rsa  ssullivan@HPTemp
ssh-rsa  zac
ssh-rsa  fauzi@uchicago.edu
ssh-rsa  thanhnd@uchicago.edu
ssh-rsa  ted.summer2@gmail.com
ssh-rsa  avantol@uchicago.edu
ssh-rsa  diw@uchicago.edu
ssh-rsa  rudyardrichter@uchicago.edu
ssh-rsa  zlchitty@uchicago.edu
ssh-rsa  emalinowski@uchicago.edu
ssh-rsa  abgeorge@umich.edu
ssh-rsa  reubenonrye@uchicago.edu
EOM
      ) | while read -r line; do
        key=$(echo $line | awk '{ print $3 }')
        if ! grep "$key" .ssh/authorized_keys > /dev/null 2>&1; then
          echo $line >> .ssh/authorized_keys
        fi
      done
    else
      echo "$dir does not exist"
    fi
  done
)
  EOF
end
